mapper.select:
SELECT QNA_CODE, LOC_CODE, QNA_CONTENT
FROM QNA
WHERE QNA_CODE=#{qna_code}

SELECT QNA_REPLY_CODE, QNA_REPLY_CONTENT
FROM QNA_REPLY
WHERE QNA_REPLY_CODE =#{qna_reply_code}

mapper.update:
UPDATE QNA_REPLY
SET QNA_REPLY_CONTENT=#{qna_reply_content}, QNA_REPLY_DATE=SYSDATE
WHERE QNA_REPLY_CODE=#{qna_reply_code}
