mapper.update:


			SELECT LOC_TYPE_CODE
            FROM LOC_TYPE
            WHERE LOC_TYPE = #{loc_type}


		UPDATE LOC_BASIC_INFO
		SET LOC_TYPE_CODE= #{LOC_TYPE_CODE}
		    , LOC_NAME=#{loc_name}
		    , LOC_SHORT_INTRO=#{loc_short_intro}
		    , LOC_INTRO=#{loc_intro}
		    , LOC_DETAIL_ADDR=#{loc_detail_addr}
		WHERE LOC_CODE = #{loc_code}
